mapper.select:
  select perms from t_auth where find_in_set(auth_id,(
  select auth_ids from t_role where role_id = (
  select role_id from  t_user where user_id = #{param1}
  )
)) and perms is not null

select * from t_auth where find_in_set(auth_id,(
  select auth_ids from t_role where role_id = (
    select role_id from  t_user where user_id = #{param1}
  )
)) and parent_id=#{param2}
